import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const PropertyForm = ({ onSubmit }) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    price: '',
    location: '',
    bedrooms: '',
    bathrooms: '',
    area: '',
    type: 'Apartamento',
    amenities: '',
    contactName: '',
    contactPhone: '',
    contactEmail: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const newProperty = {
      id: Date.now(),
      title: formData.title,
      description: formData.description,
      price: parseFloat(formData.price),
      location: formData.location,
      bedrooms: parseInt(formData.bedrooms),
      bathrooms: parseInt(formData.bathrooms),
      area: parseFloat(formData.area),
      type: formData.type,
      images: ["https://images.unsplash.com/photo-1560448204-e02f11c3d0e2?w=800"],
      amenities: formData.amenities.split(',').map(a => a.trim()),
      available: true,
      contact: {
        name: formData.contactName,
        phone: formData.contactPhone,
        email: formData.contactEmail
      }
    };

    onSubmit(newProperty);
    alert('Imóvel publicado com sucesso!');
    navigate('/');
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Publicar Novo Imóvel</h1>

      <form onSubmit={handleSubmit} style={styles.form}>
        <div style={styles.section}>
          <h2 style={styles.sectionTitle}>Informações Básicas</h2>

          <div style={styles.formGroup}>
            <label style={styles.label}>Título do Anúncio *</label>
            <input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleChange}
              required
              style={styles.input}
              placeholder="Ex: Apartamento Moderno no Centro"
            />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Descrição *</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              required
              rows="5"
              style={styles.textarea}
              placeholder="Descreva as características do imóvel..."
            />
          </div>

          <div style={styles.row}>
            <div style={styles.formGroup}>
              <label style={styles.label}>Tipo de Imóvel *</label>
              <select
                name="type"
                value={formData.type}
                onChange={handleChange}
                required
                style={styles.select}
              >
                <option>Apartamento</option>
                <option>Casa</option>
                <option>Studio</option>
                <option>Kitnet</option>
                <option>Cobertura</option>
              </select>
            </div>

            <div style={styles.formGroup}>
              <label style={styles.label}>Valor do Aluguel (R¨D) *</label>
              <input
                type="number"
                name="price"
                value={formData.price}
                onChange={handleChange}
                required
                style={styles.input}
                placeholder="2500"
              />
            </div>
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Localização *</label>
            <input
              type="text"
              name="location"
              value={formData.location}
              onChange={handleChange}
              required
              style={styles.input}
              placeholder="Bairro, Cidade - Estado"
            />
          </div>
        </div>

        <div style={styles.section}>
          <h2 style={styles.sectionTitle}>Características</h2>

          <div style={styles.row}>
            <div style={styles.formGroup}>
              <label style={styles.label}>Quartos *</label>
              <input
                type="number"
                name="bedrooms"
                value={formData.bedrooms}
                onChange={handleChange}
                required
                min="0"
                style={styles.input}
              />
            </div>

            <div style={styles.formGroup}>
              <label style={styles.label}>Banheiros *</label>
              <input
                type="number"
                name="bathrooms"
                value={formData.bathrooms}
                onChange={handleChange}
                required
                min="0"
                style={styles.input}
              />
            </div>

            <div style={styles.formGroup}>
              <label style={styles.label}>Área (m²) *</label>
              <input
                type="number"
                name="area"
                value={formData.area}
                onChange={handleChange}
                required
                min="0"
                style={styles.input}
              />
            </div>
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Comodidades</label>
            <input
              type="text"
              name="amenities"
              value={formData.amenities}
              onChange={handleChange}
              style={styles.input}
              placeholder="Garagem, Elevador, Piscina (separados por vírgula)"
            />
          </div>
        </div>

        <div style={styles.section}>
          <h2 style={styles.sectionTitle}>Informações de Contato</h2>

          <div style={styles.formGroup}>
            <label style={styles.label}>Nome *</label>
            <input
              type="text"
              name="contactName"
              value={formData.contactName}
              onChange={handleChange}
              required
              style={styles.input}
            />
          </div>

          <div style={styles.row}>
            <div style={styles.formGroup}>
              <label style={styles.label}>Telefone *</label>
              <input
                type="tel"
                name="contactPhone"
                value={formData.contactPhone}
                onChange={handleChange}
                required
                style={styles.input}
